// src/api/tasks.js
import axios from 'axios';

const getAuthHeader = () => {
  const token = localStorage.getItem('token');
  return {
    headers: { Authorization: `Bearer ${token}` },
  };
};

export const fetchTasks = () => axios.get('/api/tasks', getAuthHeader());

export const createTask = (data) =>
  axios.post('/api/tasks', data, getAuthHeader());

export const updateTask = (id, data) =>
  axios.put(`/api/tasks/${id}`, data, getAuthHeader());

export const deleteTask = (id) =>
  axios.delete(`/api/tasks/${id}`, getAuthHeader());
