import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

export default function Home() {
  const [darkMode, setDarkMode] = useState(false);

  // Toggle the dark class on <html>
  useEffect(() => {
    const html = document.documentElement;
    if (darkMode) {
      html.classList.add('dark');
    } else {
      html.classList.remove('dark');
    }
  }, [darkMode]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 to-purple-200 dark:from-gray-900 dark:to-gray-800 flex items-center justify-center px-4 transition-all">
      <div className="bg-white dark:bg-gray-900 dark:text-white shadow-2xl rounded-3xl p-10 max-w-4xl w-full text-center space-y-10 relative">

        {/* Dark Mode Toggle */}
        <div className="absolute top-4 right-4">
          <label className="flex items-center cursor-pointer">
            <span className="mr-2 text-sm text-gray-600 dark:text-gray-300">Dark Mode</span>
            <input
              type="checkbox"
              className="toggle-checkbox hidden"
              checked={darkMode}
              onChange={() => setDarkMode(!darkMode)}
            />
            <div className="w-10 h-5 bg-gray-300 rounded-full p-1 flex items-center transition-all duration-300 dark:bg-gray-700">
              <div
                className={`bg-white w-4 h-4 rounded-full shadow-md transform transition-transform duration-300 ${
                  darkMode ? 'translate-x-5' : ''
                }`}
              ></div>
            </div>
          </label>
        </div>

        {/* Title & Tagline */}
        <div>
          <h1 className="text-4xl font-extrabold text-blue-700 dark:text-blue-400 mb-2">üóÇÔ∏è Task Tracker App</h1>
          <p className="text-gray-700 dark:text-gray-300 text-lg">
            A smart way to organize, prioritize, and conquer your tasks ‚Äî with clean UI and zero distractions.
          </p>
        </div>

        {/* CTA Buttons */}
        <div className="flex justify-center gap-6">
          <Link to="/login">
            <button className="bg-blue-600 text-white px-6 py-2 rounded-xl hover:bg-blue-700 transition">Login</button>
          </Link>
          <Link to="/register">
            <button className="bg-purple-600 text-white px-6 py-2 rounded-xl hover:bg-purple-700 transition">Register</button>
          </Link>
        </div>

        {/* Feature Highlights */}
        <div className="grid sm:grid-cols-2 gap-6 text-left">
          <div className="bg-blue-100 dark:bg-gray-800 p-6 rounded-xl shadow">
            <h3 className="text-xl font-bold text-blue-700 dark:text-blue-300">üõ†Ô∏è Easy Task Management</h3>
            <p className="text-sm mt-2 text-gray-600 dark:text-gray-400">Create, edit, delete, and organize tasks effortlessly.</p>
          </div>
          <div className="bg-purple-100 dark:bg-gray-800 p-6 rounded-xl shadow">
            <h3 className="text-xl font-bold text-purple-700 dark:text-purple-300">üîê Secure Authentication</h3>
            <p className="text-sm mt-2 text-gray-600 dark:text-gray-400">JWT & bcrypt protected user access for full security.</p>
          </div>
          <div className="bg-green-100 dark:bg-gray-800 p-6 rounded-xl shadow">
            <h3 className="text-xl font-bold text-green-700 dark:text-green-300">üåç Fully Responsive</h3>
            <p className="text-sm mt-2 text-gray-600 dark:text-gray-400">Optimized layout for mobile, tablet, and desktop devices.</p>
          </div>
          <div className="bg-yellow-100 dark:bg-gray-800 p-6 rounded-xl shadow">
            <h3 className="text-xl font-bold text-yellow-700 dark:text-yellow-300">üìä Dashboard Ready</h3>
            <p className="text-sm mt-2 text-gray-600 dark:text-gray-400">Manage all your work in one place. Coming soon!</p>
          </div>
        </div>

        {/* How It Works */}
        <div>
          <h3 className="text-2xl font-bold text-blue-700 dark:text-blue-300 mb-2">‚öôÔ∏è How It Works</h3>
          <p className="text-gray-600 dark:text-gray-400">1Ô∏è‚É£ Sign up ‚Üí 2Ô∏è‚É£ Log in ‚Üí 3Ô∏è‚É£ Start managing your tasks!</p>
        </div>

        {/* Illustration */}
        <img
          src="https://illustrations.popsy.co/blue/productivity.svg"
          alt="Task management illustration"
          className="w-full max-w-md mx-auto mt-6"
        />

        {/* Footer */}
        <footer className="pt-6 border-t border-gray-300 dark:border-gray-600 text-sm text-gray-500 dark:text-gray-400">
          Built by <a href="https://github.com/kavyabg" target="_blank" rel="noopener noreferrer" className="text-blue-600 dark:text-blue-400 hover:underline">Kavya B G</a> ‚Ä¢ Follow on <a href="https://linkedin.com/in/kavyabg" target="_blank" rel="noopener noreferrer" className="text-blue-600 dark:text-blue-400 hover:underline">LinkedIn</a>
        </footer>
      </div>
    </div>
  );
}
