import React, { useState } from 'react';
import { useLoginForm } from '../forms/useLoginForm';

export default function Login() {
  const [formData, setFormData] = useState({ username: '', password: '' });
  const { handleSubmit, loading, status } = useLoginForm();

  const onSubmit = async (e) => {
    e.preventDefault();
    await handleSubmit(formData);
  };

  return (
    <form onSubmit={onSubmit}>
      <h2 className="text-3xl font-bold text-center text-red-600">Login</h2>
      <input
        type="text"
        placeholder="Username"
        value={formData.username}
        onChange={(e) => setFormData({ ...formData, username: e.target.value })}
      />
      <br />
      <input
        type="password"
        placeholder="Password"
        value={formData.password}
        onChange={(e) => setFormData({ ...formData, password: e.target.value })}
      />
      <br />
      <button type="submit" disabled={loading}>
        {loading ? 'Logging in...' : 'Login'}
      </button>
      {status.message && (
        <p style={{ color: status.success ? 'green' : 'red' }}>{status.message}</p>
      )}
    </form>
  );
}
